definition.script:
pipeline {

  agent any
  stages { 
    stage('Source MSSQL Database'){ 
        steps {  
        	 sh '''
        	 	CONTAINER_NAME="mssql_database"
				CONTAINER_FOUND="$(docker ps --all --quiet --filter=name="$CONTAINER_NAME")"
				if [ -n "$CONTAINER_FOUND" ]; then
  					docker stop $CONTAINER_FOUND && docker rm $CONTAINER_FOUND
				fi			
				docker run --name=mssql_database --network=airflow-starter -e 'ACCEPT_EULA=Y' -e 'SA_PASSWORD=4HLgdrkntTpg' -e 'MSSQL_PID=Express' -p 1433:1433 -d mcr.microsoft.com/mssql/server:2017-latest-ubuntu 
			'''

            sleep(time:25,unit:"SECONDS")

        	 sh '''  
        	    CONTAINER_NAME="mssql_database"
        	    docker exec $CONTAINER_NAME  bash -c "ln -sfn /opt/mssql-tools/bin/sqlcmd /usr/bin/sqlcmd"
				docker inspect -f '{{range .NetworkSettings.Networks}}{{.IPAddress}}{{end}}' $CONTAINER_NAME
			'''      
        }  
    } 

    stage('Clean Workspace'){ 
        steps {  
            cleanWs()
        } 
    }     

  } 

}
